description:
A simple portable .NET component library for MVVM, made for Xamarin Forms, WPF and UWP applications.

Getting started:
    public class MyViewModel : ViewModelBase
    {
        private readonly IMvvmProperty<bool> _IsWorking;
        private readonly IMvvmComputed<string> _IsBusyText;
        private readonly IMvvmListProperty<string> _Items;

        protected MyViewModel()
            : base(!App.IsRunning) // Optional. Can be used to skip calculations in design time.
        {
            _IsWorking = MvvmProperty(nameof(IsWorking), false);
            _IsBusyText = MvvmComputed(nameof(IsBusyText), () => IsWorking ? "Is busy" : "Is not busy");
            _Items = MvvmList<string>(nameof(Items));
            TestMethodCommand = MvvmCommand(nameof(TestMethodCommand), TestMethodCommandAction, () => !_IsWorking.Value);
        }

        public bool IsWorking => _IsWorking.Value;

        public string IsBusyText => _IsBusyText.Value;

        public IMvvmList<string> Items => _Items.Value;

        public IMvvmCommand TestMethodCommand { get; }

        private void TestMethodCommandAction()
        {
            _IsWorking.Value = true;
            _Items.Value.Add(DateTime.Now.ToString());
            _IsWorking.Value = false;
        }
    }
