releaseNotes:
first new ezFX5U class and ezTag
  ezFX5U myPLC = new ezFX5U();
  ezFX5U.ezTag myTag1 = new ezFX5U.ezTag();

1.Set PLC Comport data

  myPLC.portname = "COM1";
  myPLC.baudrate= "9600";
  myPLC.databits= "COM1";
  myPLC.stopbits= "1";
  myPLC.parity= "Odd";
  myPLC.readtimeout= 1000;

2.Add ErrorEvent

  myPLC.ErrorEvent += myPLC_ErrorEvent;
  void myPLC_ErrorEvent(int Code, string Message){
       MessageBox.Show(Message);        
  }
3.Connection PLC

  myPLC.ConnectServer();

4.Create Tags Datatable 

  DataTable dt = new DataTable();
  DataRow dr;
  dt.Columns.Add(new DataColumn("tag_name", typeof(string)));
  dt.Columns.Add(new DataColumn("address", typeof(string)));

  dr = dt.NewRow();
  dr["tag_name"] = "Tag1";
  dr["address"] = txt_Tag1_address.Text;
  dt.Rows.Add(dr);

  myPLC.CreateTag(dt);
  myTag1 = myPLC.sysTagList[1];
  myTag1.Active = true;
  myTag1.DataChange += myTag1_DataChange;
  myTag1.CreateTag(txt_Tag1_address.Text);


  private void myTag1_DataChange(int iIndex, string sVal, string iError, string sErrCode) {
      //sVal is PLC return value
  }


5.Start PLC Read data

  myPLC.PLCStart();
