public static void InjectKolClient(this WebApplicationBuilder builder)
        {
            var specialHeaders = new HeaderInfo[]
            {
                new HeaderInfo
                {
                    Header = Constants.ACCEPT,
                    Value = "*/*"
                }
            };

            builder.InjectHttpServiceFromBaseUrl(useUnsafe: KolUtils.GetValueFromAppSettings<bool>(builder.Configuration, "UseUnsafe"),
                specialHeaders: specialHeaders, configureAsJson: KolUtils.GetValueFromAppSettings<bool>(builder.Configuration, "ConfigureAsJson"));
        }
