public MainWindow()
        {
            InitializeComponent();
        }
        private void btnLogin_Click(object sender, RoutedEventArgs e)
        {
            string username = txtUsername.Text;
            string password = txtPassword.Password;

            // Проверка логина и пароля в базе данных или другом источнике данных
            bool isLoginSuccessful = CheckLogin(username, password);

            if (isLoginSuccessful)
            {
                // Открытие главного окна после успешного входа
                CatalogWindow catalog = new CatalogWindow();
                catalog.Show();

                // Закрытие окна входа
                Close();
            }
            else
            {
                MessageBox.Show("Invalid username or password. Please try again.", "Login Failed", MessageBoxButton.OK, MessageBoxImage.Error);
            }
        }
        private readonly HotelManagementEntities db;
        private bool CheckLogin(string username, string password)
        {
            return username == "admin" && password == "password";
        }
