private YourDbContext context; // Замените YourDbContext на ваш контекст базы данных

        public RequestsWindow()
        {
            InitializeComponent();
            context = new YourDbContext(); // Инициализируем контекст базы данных
            LoadRequests();
        }

        private void LoadRequests()
        {
            // Получаем все заявки из базы данных
            var requests = context.Requests.ToList();

            // Отображаем заявки в элементе управления (например, в DataGrid)
            dataGrid.ItemsSource = requests;
        }

        private void FilterBySeverity(string severity)
        {
            // Фильтруем заявки по степени поломки
            var filteredRequests = context.Requests.Where(r => r.Степень_поломки == severity).ToList();

            // Отображаем отфильтрованные заявки в элементе управления
            dataGrid.ItemsSource = filteredRequests;
        }

        private void ComboBox_SelectionChanged(object sender, System.Windows.Controls.SelectionChangedEventArgs e)
        {
            // Получаем выбранную степень поломки из ComboBox
            string selectedSeverity = comboBox.SelectedValue.ToString();

            // Фильтруем заявки по выбранной степени поломки
            if (selectedSeverity == "Все")
            {
                LoadRequests(); // Если выбрана опция "Все", загружаем все заявки
            }
            else
            {
                FilterBySeverity(selectedSeverity); // Иначе фильтруем по выбранной степени поломки
            }
        }
