private void Directions_SelectionChanged(object sender, SelectionChangedEventArgs e)
  {
   using (var connection = new SqlConnection("Server=(localdb)\\mssqllocaldb;Database=shoidaaaaaaaaaa;"))
   {
    if (Directions.SelectedIndex != -1)
    {
     var select = Directions.SelectedItem;
     SqlCommand command = new SqlCommand("SELECT * FROM Event INNER JOIN Directions ON Event.id = Directions.id where Directions.name = @select", connection);
     command.Parameters.AddWithValue("@select", select);
     connection.Open();
     SqlDataReader reader = command.ExecuteReader();
     DataTable dataTable = new DataTable();
     dataTable.Load(reader);  // Load data from SqlDataReader into DataTable

     EventsListView.ItemsSource = dataTable.DefaultView;  // Set DataTable as ItemsSource

    }
    else
    {
     SqlCommand command = new SqlCommand("SELECT * FROM Event INNER JOIN Directions ON Event.id = Directions.id", connection);
     connection.Open();
     SqlDataReader reader = command.ExecuteReader();
     DataTable dataTable = new DataTable();
     dataTable.Load(reader);  // Load data from SqlDataReader into DataTable

     EventsListView.ItemsSource = dataTable.DefaultView;  // Set DataTable as ItemsSource

    }
    connection.Close();
   }

  }
