private string _connectionString = @"Data Source=(localdb)\mssqllocaldb;Initial Catalog=praktikaVesna;Integrated Security=False;User ID=foder;Password=admin435;";

public EventsList()
{
    InitializeComponent();
    LoadEvents();
    LoadDirections();
}

private void LoadEvents()
{
    List<Event> events = new List<Event>();
    using (SqlConnection connection = new SqlConnection(_connectionString))
    {
        string query = "SELECT e.Id, e.Name, e.Date, d.Name as DirectionName FROM Event e JOIN Direction d ON e.DirectionId = d.Id";
        SqlCommand command = new SqlCommand(query, connection);
        connection.Open();
        SqlDataReader reader = command.ExecuteReader();
        while (reader.Read())
        {
            events.Add(new Event
            {
                Id = (int)reader["Id"],
                Name = reader["Name"].ToString(),
                Date = (DateTime)reader["Date"],
                Direction = new Direction
                {
                    Name = reader["DirectionName"].ToString()
                }
            });
        }
        reader.Close();
    }
    EventsListView.ItemsSource = events;
}

private void LoadDirections()
{
    List<Direction> directions = new List<Direction>();
    using (SqlConnection connection = new SqlConnection(_connectionString))
    {
        string query = "SELECT Id, Name FROM Direction";
        SqlCommand command = new SqlCommand(query, connection);
        connection.Open();
        SqlDataReader reader = command.ExecuteReader();
        while (reader.Read())
        {
            directions.Add(new Direction
            {
                Id = (int)reader["Id"],
                Name = reader["Name"].ToString()
            });
        }
        reader.Close();
    }
    DirectionComboBox.ItemsSource = directions;
    DirectionComboBox.DisplayMemberPath = "Name";
    DirectionComboBox.SelectedValuePath = "Id";
}

